        foreach ($completionAcceptances as $data) {
            // Find the project by name
            $project = $projectRepo->findOneBy(['projectName' => $data['projectName']]);

            if (!$project) {
                continue; // Skip if project not found
            }

            $completionAcceptance = new CompletionAcceptance();
            $completionAcceptance->setProject($project);
            $completionAcceptance->setAcceptanceDate(new \DateTimeImmutable($data['acceptanceDate']));
            $completionAcceptance->setCompletionReportDetails($data['completionReportDetails']);
            $completionAcceptance->setQualityEvaluationDetails($data['qualityEvaluationDetails']);
            $completionAcceptance->setAcceptanceInspectionDetails($data['acceptanceInspectionDetails']);
            $completionAcceptance->setDefectRectificationDetails($data['defectRectificationDetails']);
            $completionAcceptance->setFinalAccountsDetails($data['finalAccountsDetails']);
            $completionAcceptance->setArchiveDocumentationDetails($data['archiveDocumentationDetails']);

            $manager->persist($completionAcceptance);
        }

        $manager->flush();
    }
